mapper.select:
select blood_oxygen,pi,heart_rate,timestamp from zs_blood_oxygen_info
and `machine_id` = #{machineId,jdbcType=INTEGER}
ORDER BY timestamp

SELECT pi.`name` AS patientName, pi.`case_num` AS caseNum, hd.`name` AS departmentName, hr.`name` AS room, hb.`name` AS bed, record.history, record.machine_id FROM record_blood record left join record_base rb on record.base_id = rb.id LEFT JOIN patient_info pi ON rb.`patient_id` = pi.`patient_id` LEFT JOIN hospital_bed hb ON hb.`bed_id` = rb.`bed_id` LEFT JOIN hospital_room hr ON hr.`room_id` = hb.`room_id` LEFT JOIN `hospital_department` hd ON hr.`department_id` = hd.`department_id` WHERE record.status = 0 and rb.status = 0
AND record.`start_time`>= concat(#{date, jdbcType=VARCHAR} ,' 00:00:00') AND record.`start_time` <= concat(#{date, jdbcType=VARCHAR} ,' 24:00:00')
and hd.department_id = #{departmentId,jdbcType=INTEGER}
and record.`id` = #{recordId,jdbcType=INTEGER}
