mapper.select:
SELECT record.start_time,record.end_time,hd.name as departmentName FROM record_blood_pressure record left join record_base rb on record.base_id = rb.id LEFT JOIN hospital_bed hb ON rb.`bed_id` = hb.`bed_id` LEFT JOIN hospital_room hr ON hb.`room_id` = hr.`room_id` LEFT JOIN hospital_department hd ON hr.`department_id` = hd.`department_id` WHERE record.status = 0 and rb.status = 0 # and hb.status = 0 # and hr.status = 0
and hd.status = 0 and hd.department_id <> 0
and hd.department_id = #{departmentId,jdbcType=INTEGER}
and record.start_time >= #{start,jdbcType=VARCHAR}
and record.start_time <= #{end,jdbcType=VARCHAR}

select sys,dia,timestamp from zs_blood_pressure_info
and `machine_id` = #{machineId,jdbcType=INTEGER}
ORDER BY timestamp

SELECT record.`id` as recordId, data.sys, data.dia, data.update_time as timestamp, TIMESTAMPDIFF(MINUTE, record.start_time, CONCAT(CURDATE(),' ',CURTIME())) div 1440 as timeDays, TIMESTAMPDIFF(MINUTE, record.start_time, CONCAT(CURDATE(),' ',CURTIME())) mod 1440 div 60 as timeHours, TIMESTAMPDIFF(MINUTE, record.start_time, CONCAT(CURDATE(),' ',CURTIME())) MOD 60 as timeMinutes FROM record_blood_pressure record LEFT JOIN zs_blood_pressure_real data on record.machine_id = data.`machine_id`
data.update_time >= record.start_time
and record.id = #{recordId,jdbcType=INTEGER}

SELECT pi.`name` AS patientName, pi.`case_num` AS caseNum, hd.`name` AS departmentName, hr.`name` AS room, hb.`name` AS bed, record.history, record.machine_id FROM record_blood_pressure record left join record_base rb on record.base_id = rb.id LEFT JOIN patient_info pi ON rb.`patient_id` = pi.`patient_id` LEFT JOIN hospital_bed hb ON hb.`bed_id` = rb.`bed_id` LEFT JOIN hospital_room hr ON hr.`room_id` = hb.`room_id` LEFT JOIN `hospital_department` hd ON hr.`department_id` = hd.`department_id` WHERE record.status = 0 and rb.status = 0
AND record.`start_time`>= concat(#{date, jdbcType=VARCHAR} ,' 00:00:00') AND record.`start_time` <= concat(#{date, jdbcType=VARCHAR} ,' 24:00:00')
and hd.department_id = #{departmentId,jdbcType=INTEGER}
and record.`id` = #{recordId,jdbcType=INTEGER}
