mapper.select:
select temperature,timestamp from zs_temperature_info
and `machine_id` = #{machineId,jdbcType=INTEGER}
ORDER BY timestamp

SELECT record.`id` as recordId, pai.`name` as patientName ,pai.`case_num` as patientCaseNum,hd.`name` as departmentName,CONCAT(hr.`name`,hb.`name`) as roomAndBed , record.`start_time` as startTime,record.`end_time` as endTime,record.record_status as recordStatus, mti2.name as machineName FROM record_temperature record LEFT JOIN record_base rb ON record.`base_id` = rb.`id` LEFT JOIN patient_info pai ON rb.`patient_id` = pai.`patient_id` LEFT JOIN hospital_bed hb ON rb.`bed_id` = hb.`bed_id` LEFT JOIN hospital_room hr ON hb.`room_id` = hr.`room_id` LEFT JOIN hospital_department hd ON hr.`department_id` = hd.`department_id` LEFT JOIN zs_machine_info zmi ON record.`machine_id` = zmi.`machine_id` left join machine_type_info mti on zmi.machine_type_id = mti.id left join machine_type_info mti2 on mti.parent_id = mti2.id
record.status = 0
and record.created_time >= CONCAT(#{startDate, jdbcType=VARCHAR},' 00:00:00')
and record.created_time <= CONCAT(#{endDate, jdbcType=VARCHAR},' 24:00:00')
and hd.department_id = #{departmentId, jdbcType=INTEGER}
and zpi.name = #{patientName, jdbcType=VARCHAR}
