mapper.delete:
delete
from zs_ecg_info


        and machine_id = #{machineId, jdbcType = INTEGER}


        and timestamp < #{timestamp, jdbcType = BIGINT}

mapper.select:
SELECT zei.`timestamp`,
       zei.ecg
FROM `zs_ecg_info` zei
WHERE zei.machine_id = #{machineId,jdbcType=INTEGER}
  and zei.timestamp >= #{startTime,jdbcType=BIGINT}
  and zei.timestamp <= #{endTime,jdbcType=BIGINT}
ORDER BY zei.TIMESTAMP
